The example demonstrates how to use TemplateEngine to contruct E-Mail messages.
             [C#]
             
                static object GetSignature(object[] args)
                {
                    return "John Smith<br>Product Lead<br>Aspose Ltd.<br>" + DateTime.Now.ToShortDateString();
                }
                static void Main(string[] args)
                {
                    //create a new MailMessage instance as a template
                    MailMessage template = new MailMessage();
             
                    //add template field to subject
                    template.Subject = "Hello, #FirstName#";
                    template.From = "sale@aspose.com";
             
                    //add template field to receipt
                    template.To.Add(new MailAddress("#Receipt#", true));
             
                    //add template field to html body
                    //use GetSignment as the template routine, which will provide the same signment.
                    template.HtmlBody = "Dear #FirstName# #LastName#, <br><br>Thank you for your interest in <STRONG>Aspose.Network</STRONG>.<br><br>Have fun with it.<br><br>#GetSignature()#";
                    
                    //create a new TemplateEngine with the template message.
                    TemplateEngine engine = new TemplateEngine(template);
             
                    //register the GetSignment as a templet routine, for we use it in the template.
                    engine.RegisterRoutine("GetSignature", new TemplateRoutine(GetSignature));
            
                    //fill a DataTable as data source
                    DataTable dt = new DataTable();                        
                    dt.Columns.Add("Receipt", typeof(string));
                    dt.Columns.Add("FirstName", typeof(string));
                    dt.Columns.Add("LastName", typeof(string));          
            
                    DataRow dr;
                    dr = dt.NewRow();
                    dr["Receipt"] = "Nancy.Davolio<Nancy@somedomain.com>";
                    dr["FirstName"] = "Nancy";
                    dr["LastName"] = "Davolio";
                    dt.Rows.Add(dr);
                    dr = dt.NewRow();
                    dr["Receipt"] = "Andrew.Fuller<Andrew@somedomain.com>";
                    dr["FirstName"] = "Andrew";
                    dr["LastName"] = "Fuller";
                    dt.Rows.Add(dr);
                    dr = dt.NewRow();
                    dr["Receipt"] = "Janet.Leverling<Janet@somedomain.com>";
                    dr["FirstName"] = "Janet";
                    dr["LastName"] = "Leverling";          
                     dt.Rows.Add(dr);
             
                     MailMessageCollection messages;
                     try
                     {
                        //create the messages from the template and datasource.
                          messages= engine.Instantiate(dt);
                     }
                     catch (MailException ex)
                     {
                         System.Diagnostics.Debug.Write(ex.ToString());
                    }
                }
             
             [VB]
             
                Shared Function GetSignature(ByVal args() As Object) As Object
                    Return "John Smith<br>Product Lead<br>Aspose Ltd.<br>" + DateTime.Now.ToShortDateString()
                End Function
            
                Shared  Sub Main(ByVal args() As String)
                    'create a new MailMessage instance as a template
                    Dim template As MailMessage =  New MailMessage() 
             
                    'add template field to subject
                    template.Subject = "Hello, #FirstName#"
                    template.From = "sale@aspose.com"
            
                    'add template field to receipt
                    template.To.Add(New MailAddress("#Receipt#",true))
             
                    'add template field to html body
                    'use GetSignment as the template routine, which will provide the same signment.
                    template.HtmlBody = "Dear #FirstName# #LastName#, <br><br>Thank you for your interest in Aspose.Network.<br><br>Have fun with it.<br><br>#GetSignature()#";
                    'create a new TemplateEngine with the template message.
                    Dim engine As TemplateEngine =  New TemplateEngine(template) 
             
                    'register the GetSignment as a templet routine, for we use it in the template.
                    engine.RegisterRoutine("GetSignature",New TemplateRoutine(GetSignature))
            
                    'fill a DataTable as data source
                     Dim dt As DataTable =  New DataTable() 
                    dt.Columns.Add("Receipt", Type.GetType(String))
                    dt.Columns.Add("FirstName", Type.GetType(String))
                    dt.Columns.Add("LastName", Type.GetType(String))          
             
                    Dim dr As DataRow
                    dr = dt.NewRow()
                     dr["Receipt"] = "Nancy.Davolio<Nancy@somedomain.com>"
                    dr("FirstName") = "Nancy"
                    dr("LastName") = "Davolio"
                    dt.Rows.Add(dr)
                    dr = dt.NewRow()
                    dr["Receipt"] = "Andrew.Fuller<Andrew@somedomain.com>"
                    dr("FirstName") = "Andrew"
                    dr("LastName") = "Fuller"
                    dt.Rows.Add(dr)
                    dr = dt.NewRow()
                    dr["Receipt"] = "Janet.Leverling<Janet@somedomain.com>"
                    dr("FirstName") = "Janet"
                    dr("LastName") = "Leverling"          
                     dt.Rows.Add(dr)
            
                     Dim messages As MailMessageCollection
                     Try
                        'create the messages from the template and datasource.
                          messages= engine.Instantiate(dt)
                     Catch ex As MailException
                         System.Diagnostics.Debug.Write(ex.ToString())
                     End Try
                End Sub

[C#]

//Defined a function for processing your field(s)
public object ToUpper(object[] args)
{
		string name = (string)args[0];
		return name.ToUpper();
}
 
...
msg.TextBody = "Dear #FirstName#,#ToUpper(LastName)#  Hello #LastName#. Thanks";
...
TemplateEngine engine = new TemplateEngine(msg);
//Register your routine to templateengine
engine.RegisterRoutine("ToUpper", new TemplateRoutine(ToUpper));


[VB]

'Defined a function for processing your field(s)
Public Function ToPropertyName(ByVal args As Object()) As Object
	Dim name As String = CStr(args(0))
	Return name.ToUpper()
End Function
'Set up the template  ...
msg.TextBody = "Dear #FirstName#,#ToUpper(LastName)#  Hello #LastName#. Thanks"
'...
