	Graph graph1 = new Graph(sec1,100,400);
	sec1.Paragraphs.Add(graph1);

	float[] posArr = new float[]{0,0,200,80,300,40,350,90};
	Curve curve1 = new Curve(graph1,posArr);
	graph1.Shapes.Add(curve1);

	pdf1.Save(...);
	
