	sec1.Paragraphs.Add(graph1)

	Dim circle1 As Circle = New Circle(graph1, 200, 50, 30)
	graph1.Shapes.Add(circle1)

	Dim note1 As GraphNote = New GraphNote(sec1)
	note1.Content = "This is a circle."
	note1.PositionX = 160
	note1.PositionY = 10
	graph1.GraphNotes.Add(note1)
 
